def perform
    data = twitter_array
    case menu
        when 1
            nbr_joutnalist(data)
            perform
            return
        when 2
            nbr_handle(data)
            perform
            return
        when 3
            nbr_handle_aude(data)
            perform
            return
        when 4
            nbr_maj(data)
            perform
            return
        when 5
            nbr_onemaj(data)
            perform
            return
        when 6
            under(data)
            perform
            return
        when 7
            alpha_order(data)
            perform
            return
        when 8
            short_handle(data)
            perform
            return
        when 9
            position(data)
            perform
            return
        end
    
end


def menu
    puts "Choisissez une action entre 1 et 9"
    puts " "
    puts "1. Combien y a-t-il de journalistes dans ce array ?"
    puts " "
    puts "2. Combien d'entrées contiennent un numéro ?"
    puts " "
    puts "3. Combien d'entrées contiennent les 4 lettres du prénom Aude à la suite (sans prendre en compte les majuscules) ?"
    puts " "
    puts "4. Combien commencent par une majuscule (première lettre juste après le @) ?"
    puts " "
    puts "5. Combien contiennent au moins une majuscule ?"
    puts " "
    puts "6. Combien y a-t-il de underscore _ dans tous les pseudos confondus ?"
    puts " "
    puts "7. Trie la liste d'entrées par ordre alphabétique."
    puts " "
    puts "8. Quels sont les 50 entrées les plus courts de ce array ?"
    puts " "
    puts "9. Quelle est la position dans le tableau de la personne @epenser ?"
    puts " "
    print ">"
    user_input = Integer(gets)
    end

def nbr_joutnalist (data)#1
	puts "Le tableau contient #{data.size} journalistes"
    gets
end
def nbr_handle (data)#2
	puts "Le tableau contient #{data.count{|handle|handle.count("0-9")>0}} handles avec des numéros"
    gets
end
def nbr_handle_aude (data)#3 
	puts "Le tableau contient #{data.count{|handle|handle.downcase.include?"aude"}} handles avec le prénom aude"
	gets
end
def nbr_maj (data)#4 
	puts "Le tableau contient #{data.count{|handle|/[[:upper:]]/.match(handle[1])}} handles qui commencent par une majuscule"
	gets
end
def nbr_onemaj (data)#5 
	puts "#{data.count {|handle|/[[:upper:]]/.match(handle)}} handles contiennent une majuscule"
	gets
end
def under (data)#6 
	puts "#{data.count{|handle|/[[_]]/.match(handle)}} handles contiennent un underscore"
	gets
end
def alpha_order (data)#7 
	puts "Voici le tableau avec les handles triées par ordre alphabétique"
	puts data.sort #test
	gets
end
def short_handle (data)#8 
	puts "Voici les 50 handles les plus courts du tableau"
	puts data.sort_by(&:length)[0..49]
	gets
end
def position (data)#9
	puts "Le handle @epenser est à l'index : #{data.index("@epenser")}"
    gets
end
